import { createSlice } from "@reduxjs/toolkit";

const manageTask2 = createSlice({
    name: 'Task2',
    initialState: [],
    reducers: {
        addTask: (state, action) => {
            state = [...state, action.payload]
        }
    }
})
export default manageTask2.reducer;
