import { createSlice } from "@reduxjs/toolkit";

const manageTask = createSlice({
    name: 'Task',
    initialState: [],
    reducers: {
        addTask: (state, action) => {
            state.push(action.payload)
        }
        
    }
})
export const { addTask } = manageTask.actions;
export default manageTask.reducer;
